import React, {useState} from 'react';
import './App.css';


function App() {
    const [count, setCount] = useState(0)

  return (
    <div>
      <input
          id="inputNumber"
          type="number"
          value="5"
          onChange={()=>{setCount(count +1)}}
        />

      <p>Ajout : </p>
      <button onClick= {()=>{setCount(count +1)}}>
        Push + </button>

      <p>Soustrait : {useState} </p>
      <button onClick= {()=>{setCount (count -1)}}>Push -</button>
    </div>
  )
}

export default App
