import React, {useState} from 'react';
import './App.css';


function App() {
    const [count, setCount] = useState(0)

  return (
    <div>

      <p>Ajout : {count} </p>
      <button input type="number" value="5" onClick= {()=>{setCount(count +1)}}>
        Push + </button>
      <p>Soustrait : {useState} </p>
      <button onClick= {()=>{setCount (count -1)}}>Push -</button>
    </div>
  )
}

export default App
