import React, {useState} from 'react';
import './App.css';


function App() {
    const [counter, useCounter] = useState(0)

  return (
    <div>
      <p>Ajout : {useState} </p>
      <button onClick= {()=>{useCounter = counter +1}}>
        Push + </button>
      <p>Soustrait : {useState} </p>
      <button onClick= {()=>{useCounter = counter -1}}>Push -</button>
    </div>
  )
}

export default App
