import React, {useState} from 'react';
import './App.css';


function App() {
    const [count, setCount] = useState(0)

  return (
    <div>

      <p>Ajout :  </p>
      <input
          id="inputName"
          type="text"
          value="5"
          name="firstname"
          onChange={this.handleNameChange}
        />
      <button onClick= {()=>{setCount(count +1)}}>
        Push + </button>
      <p>Soustrait : {useState} </p>
      <button onClick= {()=>{setCount (count -1)}}>Push -</button>
    </div>
  )
}

export default App
